console.log('Chargement du module 1');

function addition(...nombres){
    let somme =0;
    nombres.forEach(elem =>somme += elem)
return somme;
}

function multiplication(...nombres){
    let resultat=1;
    nombres.forEach(elem =>resultat *= elem);
    return resultat;
};

module.exports = function(operation, ...nombres){
    switch(operation){
        case 'addition':
            return addition(...nombres);
            break;

        case 'multiplication':
            return multiplication(...nombres);
            break;
        
        default:
            return "Operation non-connue";
    }
};

module.exports.addition= addition;
module.exports.multiplication = multiplication;